<?php
class Utl_menu extends CI_Controller{
    
    function __construct() {
        parent::__construct();
		$this->load->model(array('m_menu'));		
		
		if(!$this->session->userdata('userid')){
			
            redirect('login');
        }
        ini_set('max_execution_time', 300);
    }
	
	function index(){        
//		$data['userid'] = ucfirst($this->session->userdata('userid'));
		redirect('utl_menu/menu');
    }
	
	function menu(){
		$data['title'] = "Daftar Menu";
		$data['record']=  $this->db->get_where('tblOL_UtlMenu',array('NotActive'=>0))->result();
		
		if ($this->uri->segment(3) == 'delete_success'){
			$data['message'] = '<div class="alert alert-success"><a class="close" data-dismiss="alert">×</a><i class="icon-info-sign">&nbsp;</i> Hapus Data Berhasil</div>';
		} elseif ($this->uri->segment(3) == 'delete_failed'){
			$data['message'] = '<div class="alert alert-danger"><a class="close" data-dismiss="alert">×</a><i class="icon-warning-sign">&nbsp;</i> Hapus Data Gagal</div>';
		} else {
			$data['message'] = $this->session->flashdata('message');
		}
		
        $this->template->display('utility/menu/menulist',$data);
	}
	
